mapper.select:
select MERCHANT_NAME, MERCHANT_LINKMAN, MERCHANT_PHONE, MERCHANT_ADDRESS, MERCHANT_QQ, USERID, INPUTDATE, MERCHANTSTATUS, EXPLAINS, BAK1, MERCHANT_ID from ZR_MERCHANT where
MERCHANT_ID = #{MERCHANT_ID}
MERCHANT_ID = #{MERCHANTID}

select m.MERCHANT_NAME, m.MERCHANT_LINKMAN, m.MERCHANT_PHONE, m.MERCHANT_ADDRESS, m.MERCHANT_QQ, m.USERID, m.INPUTDATE, m.MERCHANTSTATUS, m.EXPLAINS, m.BAK1, m.MERCHANT_ID, u.USER_ID, u.NAME FROM ZR_MERCHANT m LEFT JOIN SYS_USER u ON u.USER_ID = m.USERID WHERE 1 = 1
and m.MERCHANT_ID = #{pd.MERCHANT_ID}
and m.MERCHANT_NAME LIKE CONCAT(CONCAT('%', #{pd.MERCHANT_NAME}),'%')
and m.MERCHANTSTATUS = #{pd.MERCHANTSTATUS}
and m.INPUTDATE >= #{pd.lastLoginStart}
and m.INPUTDATE <= #{pd.lastLoginEnd}
