mapper.select:
select a.PRODUCTID, a.PACKAGEID, a.PACKAGENAME, a.COLOR, a.SIZE, a.QUANTITY, a.AMOUNT, a.CONSIGNEE, a.MOBILE, a.ADDRESS, a.POST_CODE, a.ORDER_REMARK, a.PAY_MODE, a.LOGISTICS, a.LOGISTICSNUM, a.LAST_MODIFY, a.ORDERSTATUS, a.INPUTDATE, a.BAK40, a.MERCHANTID, a.IP, p.PRODUCT_NAME, a.ORDERS_ID from ZR_ORDERS a JOIN zr_products p on p.PRODUCTS_ID=a.PRODUCTID where 1=1
and a.CONSIGNEE LIKE CONCAT(CONCAT('%', #{pd.CONSIGNEE}),'%')
and a.MOBILE LIKE CONCAT(CONCAT('%', #{pd.MOBILE}),'%')
and a.PRODUCTID=#{pd.PRODUCTID}
and a.MOBILE=#{pd.ORDERSTATUS}
and a.MERCHANTID=#{pd.MERCHANTID}
and and substr(a.INPUTDATE,1,10) >= #{pd.lastLoginStart}
and substr(a.INPUTDATE,1,10) <= #{pd.lastLoginEnd}
