mapper.select:
select pa.PRODUCTID, pa.PACKAGE_NAME, pa.PACKAGE_PRICE, pa.BAK30, pa.PACKAGES_ID, pr.PRODUCTS_ID, pr.PRODUCT_NAME from ZR_PACKAGES pa LEFT JOIN ZR_PRODUCTS pr on pr.PRODUCTS_ID = pa.PRODUCTID where 1=1
and pa.PACKAGES_ID = #{PACKAGES_ID}
and pa.PRODUCTID = #{PRODUCTS_ID}

select pa.PRODUCTID, pa.PACKAGE_NAME, pa.PACKAGE_PRICE, pa.BAK30, pa.PACKAGES_ID, pr.PRODUCTS_ID, pr.PRODUCT_NAME from ZR_PACKAGES pa LEFT JOIN ZR_PRODUCTS pr on pr.PRODUCTS_ID = pa.PRODUCTID where 1 = 1
and pr.PRODUCTS_ID = #{pd.PRODUCTSID}
and pa.PACKAGE_NAME LIKE CONCAT(CONCAT('%', #{pd.PACKAGE_NAME}),'%')

select a.PRODUCTID, a.PACKAGE_NAME, a.PACKAGE_PRICE, a.BAK30, a.PACKAGES_ID from ZR_PACKAGES a where 1=1
and a.PRODUCTID=#{PRODUCTID}
order by a.PACKAGES_ID
